<h1>Productos</h1>

<div class="contenido">
    <div *ngFor="let producto of productos">

        <h3>
            <a
              [title]="producto.name + ' details'"
              [routerLink]="['/products', producto.id]">
              {{ producto.name }}
            </a>
          </h3>

        <div class="product">
           <app-product [producto]="producto"></app-product>
       </div>
    </div>
</div>


