<h1>Productos</h1>

<div class="contenido">
    <div *ngFor="let producto of productos">
        <div class="product">
           <app-product [producto]="producto"></app-product>
           <button type="button" (click)="compartir()">Compartir</button>

           <app-product-alerts [producto]="producto" (notify)="onNotify()">
           </app-product-alerts>
       </div>
       
    </div>
</div>


